@theme {
  /* Colors */
  --color-primary-50: oklch(18.54% 0.122 277.1);
  --color-primary-100: oklch(26.54% 0.139 277.1);
  --color-primary-200: oklch(34.54% 0.155 277.1);
  --color-primary-300: oklch(42.54% 0.171 277.1);
  --color-primary-400: oklch(50.54% 0.188 277.1);
  --color-primary-500: oklch(58.54% 0.204 277.1);
  --color-primary-600: oklch(66.54% 0.188 277.1);
  --color-primary-700: oklch(74.54% 0.171 277.1);
  --color-primary-800: oklch(82.54% 0.155 277.1);
  --color-primary-900: oklch(90.54% 0.139 277.1);
  --color-on-light: #deef188;
  --color-on-dark: #0d-1330;
  --color-light-bg: #e7ed10b;
  --color-dark-bg: #10-194d;

  /* Typography */
  --font-size-xs: 6.11px;
  --font-size-sm: 9.89px;
  --font-size-base: 16px;
  --font-size-lg: 25.89px;
  --font-size-xl: 41.89px;
  --font-size-2xl: 67.77px;
  --font-size-3xl: 109.66px;
  --font-size-4xl: 177.42px;
  --font-size-5xl: 287.07px;

  /* Fluid Typography */
  --font-size-xs-fluid: clamp(5.50px, 5.25px + 0.08vw, 6.72px);
  --font-size-sm-fluid: clamp(8.90px, 8.51px + 0.12vw, 10.88px);
  --font-size-base-fluid: clamp(14.40px, 13.76px + 0.20vw, 17.60px);
  --font-size-lg-fluid: clamp(23.30px, 22.27px + 0.32vw, 28.48px);
  --font-size-xl-fluid: clamp(37.70px, 36.03px + 0.52vw, 46.08px);
  --font-size-2xl-fluid: clamp(60.99px, 58.28px + 0.85vw, 74.55px);
  --font-size-3xl-fluid: clamp(98.69px, 94.31px + 1.37vw, 120.63px);
  --font-size-4xl-fluid: clamp(159.68px, 152.58px + 2.22vw, 195.16px);
  --font-size-5xl-fluid: clamp(258.36px, 246.88px + 3.59vw, 315.78px);

  /* Line Heights */
  --leading: 1.5;

  /* Spacing */
  --spacing-0: 0rem;
  --spacing-1: 0.125rem;
  --spacing-2: 0.25rem;
  --spacing-3: 0.375rem;
  --spacing-4: 0.5rem;
  --spacing-5: 0.625rem;
  --spacing-6: 0.75rem;
  --spacing-8: 1rem;
  --spacing-10: 1.25rem;
  --spacing-12: 1.5rem;
  --spacing-16: 2rem;
  --spacing-20: 2.5rem;
  --spacing-24: 3rem;

  /* Breakpoints */
  --breakpoint-xs: 320px;
  --breakpoint-sm: 640px;
  --breakpoint-md: 768px;
  --breakpoint-lg: 1024px;
  --breakpoint-xl: 1280px;
  --breakpoint-2xl: 1536px;

  /* Grid */
  --grid-columns: 12;

  /* Elevation (shadows) */
  --shadow-none: none;
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);

  /* Animation */
  --duration-fast: 100ms;
  --duration-base: 150ms;
  --duration-slow: 300ms;
  --easing: cubic-bezier(0.4, 0, 0.2, 1);
}

/* Utility Classes */
.text-primary { color: var(--color-primary-500); }
.bg-primary { background-color: var(--color-primary-500); }
.border-primary { border-color: var(--color-primary-500); }
