@theme {
  /* Colors */
  --color-primary-50: oklch(20.89% 0.067 221.7);
  --color-primary-100: oklch(28.89% 0.075 221.7);
  --color-primary-200: oklch(36.89% 0.084 221.7);
  --color-primary-300: oklch(44.89% 0.093 221.7);
  --color-primary-400: oklch(52.89% 0.102 221.7);
  --color-primary-500: oklch(60.89% 0.111 221.7);
  --color-primary-600: oklch(68.89% 0.102 221.7);
  --color-primary-700: oklch(76.89% 0.093 221.7);
  --color-primary-800: oklch(84.89% 0.084 221.7);
  --color-primary-900: oklch(92.89% 0.075 221.7);
  --color-on-light: #94100123;
  --color-on-dark: #-1-106;
  --color-light-bg: #dff2f9;
  --color-dark-bg: #-110f25;

  /* Typography */
  --font-size-xs: 9px;
  --font-size-sm: 12px;
  --font-size-base: 16px;
  --font-size-lg: 21.33px;
  --font-size-xl: 28.43px;
  --font-size-2xl: 37.9px;
  --font-size-3xl: 50.52px;
  --font-size-4xl: 67.34px;
  --font-size-5xl: 89.76px;

  /* Fluid Typography */
  --font-size-xs-fluid: clamp(8.10px, 7.74px + 0.11vw, 9.90px);
  --font-size-sm-fluid: clamp(10.80px, 10.32px + 0.15vw, 13.20px);
  --font-size-base-fluid: clamp(14.40px, 13.76px + 0.20vw, 17.60px);
  --font-size-lg-fluid: clamp(19.20px, 18.34px + 0.27vw, 23.46px);
  --font-size-xl-fluid: clamp(25.59px, 24.45px + 0.36vw, 31.27px);
  --font-size-2xl-fluid: clamp(34.11px, 32.59px + 0.47vw, 41.69px);
  --font-size-3xl-fluid: clamp(45.47px, 43.45px + 0.63vw, 55.57px);
  --font-size-4xl-fluid: clamp(60.61px, 57.91px + 0.84vw, 74.07px);
  --font-size-5xl-fluid: clamp(80.78px, 77.19px + 1.12vw, 98.74px);

  /* Line Heights */
  --leading: 1.5;

  /* Spacing */
  --spacing-0: 0rem;
  --spacing-1: 0.125rem;
  --spacing-2: 0.25rem;
  --spacing-3: 0.375rem;
  --spacing-4: 0.5rem;
  --spacing-5: 0.625rem;
  --spacing-6: 0.75rem;
  --spacing-8: 1rem;
  --spacing-10: 1.25rem;
  --spacing-12: 1.5rem;
  --spacing-16: 2rem;
  --spacing-20: 2.5rem;
  --spacing-24: 3rem;

  /* Breakpoints */
  --breakpoint-xs: 320px;
  --breakpoint-sm: 640px;
  --breakpoint-md: 768px;
  --breakpoint-lg: 1024px;
  --breakpoint-xl: 1280px;
  --breakpoint-2xl: 1536px;

  /* Grid */
  --grid-columns: 12;

  /* Elevation (shadows) */
  --shadow-none: none;
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);

  /* Animation */
  --duration-fast: 100ms;
  --duration-base: 150ms;
  --duration-slow: 300ms;
  --easing: cubic-bezier(0.4, 0, 0.2, 1);
}

/* Utility Classes */
.text-primary { color: var(--color-primary-500); }
.bg-primary { background-color: var(--color-primary-500); }
.border-primary { border-color: var(--color-primary-500); }
